mapper.select:
	SELECT ID, BEAN_ID, METHOD_NAME, `GROUP`, EXPRESSION, DESCRIPTION, STATUS, CREATETIME, LASTUPDATE FROM JOBTASK 

        BEANID = #{BEAN_ID}
        AND METHOD_NAME = #{methodName}
        AND `GROUP` = #{group}
        AND STATUS = #{status, typeHandler = org.apache.ibatis.type.EnumOrdinalTypeHandler}
